def median_and_means(measures):
    """
    To get an accurate summary of a set of measure, this function computes the
    following value and returns them in a tuple:
        - median
        - mean (average)
        - mean of the values between the first quartile and third quartile

    E.g., median_and_means([1,2,3,4,5,6,7,8,9]) is (5, 5, 5)

    Note: if measures is an empty list, your function should return the object None.

    :param measures: list of measures
    :return: tuple (med, mean, mean_50) where med is the median of the values
    in measures, mean is the mean and mean_50 the mean of the 50% of value in
    the middle of the range.
    :rtype: tuple
    """
    def cal_mean(p_measures):
        lmedian = float(0)
        llen = 0
        lmiddle = 0
        lmeasures = p_measures
        lmeasures.sort()
        llen = len(lmeasures)
        lmiddle = llen//2
        if (llen % 2) == 1:
            lmedian = lmeasures[lmiddle]
        else:
            lmedian =  round(((lmeasures[lmiddle-1] + lmeasures[lmiddle])/2),2)
        return lmeasures, llen, lmiddle, lmedian

    l_mean = float(0)
    l_meanq = float(0)
    l_len = 0
    l_middle = 0
    l_q1med = 0
    l_q3mmed = 0
    l_qmid = 0
    l_measures = measures
    l_len = len(l_measures)
    q1_middle = 0 
    q3_middle = 0 
    if l_measures == []:
        return None
    l_measures, l_len, l_middle, l_median = cal_mean(measures)

    for num in range(l_len):
        l_mean += l_measures[num]
    l_mean = l_mean/(l_len)
    q1_middle = l_middle
    q3_middle = l_middle 
    if (l_len % 2) != 0:
        q1_middle = l_middle+1

    l_q1 = l_measures[0:q1_middle]
    l_q1, l_len, l_qmid, l_q1med = cal_mean(l_q1)
    l_q3 = l_measures[q3_middle:]
    l_q3, l_len, l_qmid, l_q3med = cal_mean(l_q3)
    l_meanq = (l_q1med+l_q3med)/2
    return (l_median, l_mean, l_meanq)


